import React, {Component} from 'react';
import axios from 'axios';

import Global from '../Global'


class Articles extends Component{


	state = {
		articles: [],
		status: null,
	};



	componentWillMount(){
		this.getArticles();
	}


	getArticles = () => {
		axios.get('http://localhost:3900/api/articles')
			.then( res => {
				this.setState({
					articles: res.data.articles,
					status: 'success'
				});
			});
	};




	render(){

		if(this.state.articles.length > 0){
			let listArticles = this.state.articles.map( (article) => {
				return (
					<article className="article-item" id="article-template">
                        <div className="image-wrap">
                            <img src="https://unhabitatmejor.leroymerlin.es/sites/default/files/styles/header_category/public/2018-10/4%20paisaje%20macedonia.jpg?itok=AELknmF8" alt="Paisaje" />
                        </div>

                        <h2> {article.title} </h2>
                        <span className="date">
                            {article.date}
                        </span>
                        <a href="#">Leer más</a>

                        <div className="clearfix"></div>
                    </article>
				);
			});
			return (
				<div id="articles">
					{listArticles}
				</div>
			);

		} else if(this.state.articles.length === 0 && this.state.status === 'success'){
			return (
				<div id="articles">
					<h2 className="subheader">No hay artículos para mostrar</h2>
					<p>Todavía no hay contenido en esta sección.</p>
				</div>
			);
		} else{
			return (
				<div id="articles">
					<h2 className="subheader">Cargando...</h2>
					<p>Espere mintras carga el contenido.</p>
				</div>
			);
		}	
	}
}


export default Articles;
