import React, {Component} from 'react';
import {Redirect, Link} from 'react-router-dom';
import axios from 'axios';
import Moment from 'react-moment';
import 'moment/locale/es';

import Global from '../Global';
import Sidebar from './Sidebar';



class Article extends Component{

	url = Global.url;

	state = {
		article: false,
		status: null
	};


	componentWillMount(){
		this.getArticle();
	}


	getArticle = () => {
		let id = this.props.match.params.id;
		let endpoint = this.url + '/article/' + id;

		axios.get(endpoint)
		.then(res => {
			this.setState({
				article: res.data.article,
				status: 'success'
			});
		});
	};


	render(){
		const article = this.state.article;
		return (
			<div className="center">
	            <section id="content">
	            	{ article ? ( 
	                    <article className="article-item article-detail">
	                        <div className="image-wrap">
	                            <img src="https://unhabitatmejor.leroymerlin.es/sites/default/files/styles/header_category/public/2018-10/4%20paisaje%20macedonia.jpg?itok=AELknmF8" alt="Paisaje" />
	                        </div>

	                        <h1 className="subheader"> {article.title} </h1>
	                        <span className="date">
	                        	<Moment locale="es" fromNow> {article.date} </Moment>
	                        </span>
	                        <p>
	                        	{article.content}    
	                        </p>

	                        <div className="clearfix"></div>
	                    </article> 

	                    ) : (
	                    	<article>
	                    		<h2 className="subheader">El artículo no existe</h2>
	                    		<p>No se ha encontrado ningún artículo.</p>
	                    	</article>
	                    )
	            	}
	            </section>

	            <Sidebar />

	            <div className="clearfix"></div>
	        </div>
		);
	}
}


export default Article;
