import React, {Component} from 'react';
import axios from 'axios';
import Moment from 'react-moment';
//import 'moment/locale/es';

import Global from '../Global'
import ImageDefault from '../assets/images/default.png';


class Articles extends Component{

	url = Global.url;



 today = new Date();
 date = this.today.getFullYear()+'-'+(this.today.getMonth()+1)+'-'+this.today.getDate();


	state = {
		articles: [],
		status: null,
	};


	componentWillMount(){
		this.getArticles();
	}


	getArticles = () => {
		let endpoint = this.url + 'articles';
		axios.get(endpoint)
			.then( res => {
				this.setState({
					articles: res.data.articles,
					status: 'success'
				});
			});
	};

	dateFormater(date){
		let fdate= new Date(date);
		console.log(fdate.toString());
		return fdate;
	}




	render(){

		if(this.state.articles.length > 0){
			let listArticles = this.state.articles.map( (article) => {
				return (
					<article className="article-item" id="article-template">
                        <div className="image-wrap">
                        	{ 
                        		article.image != null ? (
	                        		<img src={this.url+'get-image/'+article.image} alt={article.title}/>
	                        	) : (
	                        		<img src={ImageDefault}/>
	                        	)
	                        }
                        </div>

                        <h2> {article.title} </h2>

                        <span className="date">

                        <Moment>
                        	Wed Dec 22 2021 15:30:41 GMT+0100 (hora estándar de Europa central)
                        </Moment>

                        <br/>

                        { this.dateFormater(article.date).toString() }




                        	<br/>

                        	{ article.date }

                        </span>
                        <a href="#">Leer más</a>

                        <div className="clearfix"></div>
                    </article>
				);
			});
			return (
				<div id="articles">
					{listArticles}
				</div>
			);

		} else if(this.state.articles.length === 0 && this.state.status === 'success'){
			return (
				<div id="articles">
					<h2 className="subheader">No hay artículos para mostrar</h2>
					<p>Todavía no hay contenido en esta sección.</p>
				</div>
			);
		} else{
			return (
				<div id="articles">
					<h2 className="subheader">Cargando...</h2>
					<p>Espere mintras carga el contenido.</p>
				</div>
			);
		}	
	}
}


export default Articles;
